import React from 'react'


const Jmod = (props) => {

    function handleReddit (e) {
        e.preventDefault()
        let token = localStorage.getItem("token")
        fetch('http://localhost:3000/reddit', {
            method: "POST",
            headers: {
                "Authorization": `Bearer ${token}`,
                "Content-Type" : "application/json",
                "Accept" : "application/json"
            },
            body: JSON.stringify({
              name: `${props.jmod.name}`
            })

        })
        .then(res => res.json())
        .then(res => this.setState({
             comments: res.data.children
        }
        ))
    }

    function handleTwitter  (e)  {
        e.preventDefault()
        let token = localStorage.getItem("token")
        fetch('http://localhost:3000/search', {
            method: "POST",
            headers: {
                "Authorization": `Bearer ${token}`,
                "Content-Type" : "application/json",
                "Accept" : "application/json"
            },
            body: JSON.stringify({
              name: `${props.jmod.name}`
            })

        })
        .then(res => res.json())
        .then(console.log)
    }
    //to iterate over twitter response
    //response comes out as
    //{data: [{"text": "blah blah", "id": "number_string"}]} 10 objects return inside the array.
    //to generate link to tweet:
    //https://twitter.com/${username}/status/${id}


    //when user doesn't exist, return is:
    //{errors:{detail: "blah blah blah"}}
    //to iterate over reddit response
    //response comes out as
    //{data: {children: [{data: {body: text, permalink: text}}, {}, {}]}}
    //to generate link to comment:
    //https://reddit.com/${permalink}
    //if user isn't found, response is {message: "Not Found"}

    return (
        <div>
        <h1>{props.jmod.name}</h1>
        <button class="btn btn-primary" onClick={(e) => {handleTwitter(e)}}>Twitter</button> <button class="btn btn-primary" onClick={(e) => {handleReddit(e)}}>Reddit</button> 
        </div>
    )
}
export default Jmod
