import React from 'react'


const Jmod = (props) => {

    handleReddit = (e) => {
        e.preventDefault()
        let token = localStorage.getItem("token")
        console.log(`sending reddit request for ${this.state.query}`)
        fetch('http://localhost:3000/reddit', {
            method: "POST",
            headers: {
                "Authorization": `Bearer ${token}`,
                "Content-Type" : "application/json",
                "Accept" : "application/json"
            },
            body: JSON.stringify({
              name: `${this.state.query}`
            })

        })
        .then(res => res.json())
        .then(res => this.setState({
             comments: res.data.children
        }
        ))
    }
    //to iterate over reddit response
    //response comes out as
    //{data: {children: [{data: {body: text, permalink: text}}, {}, {}]}}
    //to generate link to comment:
    //https://reddit.com/${permalink}
    //if user isn't found, response is {message: "Not Found"}

    return (
        <div>
        <h1>{props.jmod.name}</h1>
        <button class="btn btn-primary" onClick={(e) => {this.handleTwitter}}>Twitter</button> <button class="btn btn-primary" onClick={(e) => {this.handleReddit}}>Reddit</button> 
        </div>
    )
}
export default Jmod
