import React from 'react'
import Form from 'react-bootstrap/Form'


export default class Search extends React.Component {
    state = {
        query: '',
        id: 0
    }
    handleChange = (e) => {
        let query =  e.target.value
        this.setState({query})
      }

    handleSubmit = (e) => {
        e.preventDefault()
        fetch(`https://api.twitter.com/2/users/1712662364/tweets?tweet.fields=created_at&expansions=author_id&user.fields=created_at&max_results=10`, {
            method: "GET",
            headers: {
                "Authorization" : `Bearer AAAAAAAAAAAAAAAAAAAAAHsSNAEAAAAADFg2%2F6Rfe3DttuBGLp1R7cLuzhs%3D8YqO9JvIJE6efcNs95l9G1M0Xg7Qou73y9rfg87rIbAzUnXFLs`,
                "Content-Type" : "application/json",
                "Accept" : "application/json"
            }
        }).then(res => res.json())
        .then(console.log)
    }

    render(){
        return (
            <div>
            <Form onSubmit={this.handleSubmit}>
                <Form.Group>
                    <Form.Control type="text" placeholder="Search" onChange={this.handleChange}/>
                </Form.Group>
            </Form>
            <p>{this.state.id}</p>
            </div>
        )
    }
}
