import React from 'react'
import CommentContainer from '../containers/CommentContainer'
import TweetContainer from '../containers/TweetContainer'


class Jmod extends React.Component {

    state = {
        active: ""
    }

    handleReddit (e) {
        e.preventDefault()
        let token = localStorage.getItem("token")
        fetch('http://localhost:3000/reddit', {
            method: "POST",
            headers: {
                "Authorization": `Bearer ${token}`,
                "Content-Type" : "application/json",
                "Accept" : "application/json"
            },
            body: JSON.stringify({
              name: `${this.props.jmod.name}`
            })

        })
        .then(res => res.json())
        .then(res => {
            if(res.data && res.data.children.length > 0) {
                this.setState({
                    comments: res.data.children,
                    active: "reddit",
                    error: ""
                })
            }   
            else{
                this.setState({
                    error: "This person does not comment on Reddit"
                })
            } 
        })
    }

     handleTwitter  (e)  {
        e.preventDefault()
        let token = localStorage.getItem("token")
        fetch('http://localhost:3000/search', {
            method: "POST",
            headers: {
                "Authorization": `Bearer ${token}`,
                "Content-Type" : "application/json",
                "Accept" : "application/json"
            },
            body: JSON.stringify({
              name: `${this.props.jmod.name}`
            })

        })
        .then(res => res.json())
        .then(tweets => {
            if (tweets.data) {
                this.setState({
                    active: "twitter",
                    tweets: tweets.data,
                    error: ""
                })
            }
            else {
                this.setState({
                    error: "This person does not visit Twitter"
                })
            }
        })
    }


    //to iterate over twitter response
    //response comes out as
    //{data: [{"text": "blah blah", "id": "number_string"}]} 10 objects return inside the array.
    //to generate link to tweet:
    //https://twitter.com/${username}/status/${id}


    //when user doesn't exist, return is:
    //{errors:{detail: "blah blah blah"}}
    //to iterate over reddit response
    //response comes out as
    //{data: {children: [{data: {body: text, permalink: text}}, {}, {}]}}
    //to generate link to comment:
    //https://reddit.com/${permalink}
    //if user isn't found, response is {message: "Not Found"}

    renderSwitch(state) {
        switch(state) {
            case "reddit":
                return <CommentContainer comments={this.state.comments} jmod={this.props.jmod}/>
            case "twitter":
                return <TweetContainer tweets={this.state.tweets} jmod={this.props.jmod}/>
            default:
                return ""
        }
    }

    displayFollow() {
        let follows = localStorage.getItem('follows')
        //does the user have a list of follows? parse it otherwise create a blank list.

        //if the jmod appears in list of follows, display unfollow button. else display follow button
        this.handleFollow(follows)
    }

    handleFollow(e) {
        let follows = localStorage.getItem('follows')
        console.log('checkpoint 1')
        follows ? null : localStorage.setItem('follows', '')
        console.log('checkpoint 2')

            console.log('checkpoint 3')
            if (follows.length > 0) {
                console.log('checkpoint 4')
                let followsArray = []
                followsArray.push(JSON.parse(follows))
                console.log(followsArray)
                if (followsArray.find(jmod => jmod===`${this.props.jmod.name}`)){           
                    //remove the index of follows where jmod's name is located, stringify, then set to local storage
                    localStorage.setItem('follows', JSON.stringify(followsArray.splice(followsArray.findIndex(jmod => jmod===`${this.props.jmod.name}`), 1)))
                    console.log('made it to remove follow')
                }
                else {     
                     followsArray.push(this.props.jmod.name)
                     localStorage.setItem('follows', JSON.stringify(followsArray))
                     console.log('made it to add follow')
                }
            }
            else {
                follows = follows+=(`${this.props.jmod.name}`)
                    localStorage.setItem('follows', JSON.stringify(follows))
                    console.log('made it to third condition')
            }    
    }

    render(){
    return (

        <div className="JmodPage">
            <h5>{this.state.error ? this.state.error : null}</h5>
        <h1>{this.props.jmod.name}</h1>
        <button className="follow" onClick={(e) => {this.handleFollow(e)}}></button>
        <button className="twitter" onClick={(e) => {this.handleTwitter(e)}}></button> <button className="reddit" onClick={(e) => {this.handleReddit(e)}}></button>
        {this.renderSwitch(this.state.active)}
        </div>
    )
    }
}
export default Jmod
