import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css'
import React, {Component} from 'react'
import { BrowserRouter as Router, Route } from 'react-router-dom'
import NavigationBar from '../components/NavigationBar'
import Profile from '../components/Profile'
import HomePage from '../components/HomePage'
import HomeContainer from './HomeContainer'
import JmodPage from './JmodPage'
import FeedPage from './FeedPage'

class App extends Component {

  state = {
    jmod: "",
    query: "",
    follows: []
  }

  handleChange = (e) => {
    let query =  e.target.value
    this.setState({query})
  }

  searchMod = (e) => {
    e.preventDefault()
    let token = localStorage.getItem("token")
    fetch('http://localhost:3000/jmods', {
        method: "POST",
        headers: {
            "Authorization": `Bearer ${token}`,
            "Content-Type" : "application/json",
            "Accept" : "application/json"
        },
        body: JSON.stringify({
            name: `${this.state.query}`
        })
    })
    .then(res => res.json())
    .then(jmod => this.setState({jmod}))
}


  componentDidMount() {
    let follows = localStorage.getItem('follows')
    if (follows) {
      this.setState(prevState => ({
        follows: JSON.parse(follows)
      }))
    }
  }

  handleUserInfo = (userId) => {
    this.setState({userId})
  }

  activeMod = (jmod) => {
    this.setState({jmod})

}

  handleFollow(e) {
    let follows = localStorage.getItem('follows')
    if (follows){      
    } 
    else {
        localStorage.setItem('follows', '')
          follows = localStorage.getItem('follows')
    }  
        if (follows.length > 0) {
            follows = JSON.parse(follows)

            if (follows.find(jmod => jmod===`${this.props.jmod}`)){ 
                //remove the index of follows where jmod's name is located, stringify, then set to local storage
                let index = follows.findIndex(jmod => jmod===`${this.props.jmod}`)
                follows.splice(index, 1)
                localStorage.setItem('follows', JSON.stringify(follows))
                this.setState(prevState => ({
                    error: `No longer following ${this.props.jmod}`
                }))
            }
            else {     
                follows.push(this.props.jmod)
                localStorage.setItem('follows', JSON.stringify(follows))
                this.setState(prevState => ({
                    error: `Now following ${this.props.jmod}`
                }))

            }
        }
        else {
            follows = [`${this.props.jmod}`]
                localStorage.setItem('follows', JSON.stringify(follows))
                this.setState(prevState => ({
                    error: `Now following ${this.props.jmod}`
                }))
        }
        // this.setState(prevState => ({
        //     follows: { 
        //         ...prevState.follows, follows
        //     }
        // }))
}



  render() {
    return (

      <Router>
        <div  >
          <NavigationBar signOut={this.signOut} searchMod={this.searchMod} handleChange={this.handleChange}/>
          <div className="test">
          <Route exact path='/' render={routerProps => <HomeContainer {...routerProps} handleUserInfo={this.handleUserInfo} />} />
          <Route exact path='/home' render={routerProps => <HomePage {...routerProps}/>  } />
          <Route exact path='/jmods' render={routerProps => <JmodPage {...routerProps} jmod={this.state.jmod} activeMod={this.activeMod} handleFollow={this.handleFollow()}/>}/>
          <Route exact path='/feed' render={routerProps => <FeedPage {...routerProps} jmod={this.state.jmod} follows={this.state.follows} activeMod={this.activeMod} handleFollow={this.handleFollow()}/>  } />
          <Route exact path='/profile' render={routerProps => <Profile {...routerProps} /> } />
          </div>
        </div>
      </Router>
    );
  }



}

export default App;
