import React from 'react'

    const Tweet = (props) => {

       function createTweet  (tweet, jmod)  {
            let token = localStorage.getItem("token")
            fetch('http://localhost:3000/tweets', {
                method: "POST",
                headers: {
                    "Authorization": `Bearer ${token}`,
                    "Content-Type" : "application/json",
                    "Accept" : "application/json"
                },
                body: JSON.stringify({
                    body: `${tweet.text}`,
                    url: `https://twitter.com/${jmod.name}/status/${tweet.id}`,
                    jmod_id: `${jmod.id}`    
                })
            })
            .then(res => res.json())
            .then(console.log())
        }

            return (
                 <div className="card" width="15%">
                    <p>{props.tweet.text}</p>
                    <a href={`https://twitter.com/${props.jmod}/status/${props.tweet.id} `}>{`https://twitter.com/${props.jmod}/status/${props.tweet.id}`}</a>
                    <button onClick={() => {createTweet(props.tweet, props.jmod)}}>pin</button>
                 </div>

            )
}
export default Tweet
