import React from 'react'

    const PinComment = (props) => {

        function destroyComment  (comment)  {
            let token = localStorage.getItem("token")
            fetch(`http://localhost:3000/comments/${comment.id}`, {
                method: "DELETE",
                headers: {
                    "Authorization": `Bearer ${token}`,
                }
            })
            .then(res => res.json())
            .then(console.log())
        }

            return (
                    <div className="card">
                        <p>{props.comment.body}</p>
                        <a href={`https://reddit.com${props.comment.permalink}`}>https://reddit.com{props.comment.permalink}</a>
                        <button onClick={destroyComment(props.comment)}>delete</button>
                    </div>

            )
}
export default PinComment
