import React ,{Component} from 'react'
import CommentContainer from '../containers/CommentContainer'
import TweetContainer from '../containers/TweetContainer'

class Profile extends Component {

    state = {
        active: ""
    }

    renderSwitch(state) {
        switch(state) {
            case "reddit":
                return <CommentContainer comments={this.state.comments} jmod={this.props.jmod}/>
            case "twitter":
                return <TweetContainer tweets={this.state.tweets} jmod={this.props.jmod}/>
            default:
                return ""
        }
    }

    componentDidMount() {
        this.checkAuth()
     }

     checkAuth = () => {
         let token = localStorage.getItem("token")
        token ? console.log("hi") : this.props.history.push("/")
     }

    render() {
        return(
            <div>
                <h1>This is the Users Profile</h1>
            </div>
        )
    }
}

export default Profile
